( function( $ ) {
	$( function() {
		$( '.main-navigation .handheld-navigation, .main-navigation div.menu' ).prepend( '<span id="close-menu" class="shm-close"> Cerrar </span>' );

        // MENU - romel
        function toggled(){
            // console.log('toggled start');
            if(jQuery("#site-navigation").hasClass("toggled")){
                jQuery("#site-navigation").removeClass("toggled");
            }else{
                jQuery("#site-navigation").addClass("toggled");
            }
            // console.log('toggled end');
        }
	
        jQuery("#close-menu").click(function() {
            toggled();
        });

	} );
} )( jQuery );